import React, { useState } from "react";
import { Icon } from "@iconify/react";
import SectionHeading from "./SectionHeading";

export default function Awards({ data }) {
  const [modal, setModal] = useState(false);
  const [modalData, setModalData] = useState({});
  const { sectionHeading, allAwards } = data;

  const handleAwardDetails = (item) => {
    setModalData(item);
    setModal(!modal);
  };

  return (
    <>
      <section className="section gray-bg">
        <div className="container">
          <SectionHeading
            miniTitle={sectionHeading.miniTitle}
            title={sectionHeading.title}
          />
          <div className="row gy-3">
            {allAwards?.map((award, index) => (
              <div
                className="col-md-6 col-lg-4"
                key={index}
                data-aos="fade-up"
                data-aos-duration="1200"
                data-aos-delay={index * 100}
              >
                <div className="award-box">
                  <div className="award-image">
                    <img
                      src={award.image}
                      alt={award.title}
                      className="img-fluid rounded"
                    />
                    <span
                      className="gallery-link"
                      onClick={() => handleAwardDetails(award)}
                    >
                      <i>
                        <Icon icon="bi:plus" />
                      </i>
                    </span>
                  </div>
                  <div className="award-content">
                    <h4>{award.title}</h4>
                    <span className="award-date">{award.date}</span>
                    <p>{award.description}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Modal for Larger Image View */}
      {modal && (
        <div className="mfp-wrap">
          <div className="mfp-container">
            <div className="mfp-bg" onClick={() => setModal(!modal)}></div>
            <div className="mfp-content">
              <button
                type="button"
                className="mfp-close"
                onClick={() => setModal(!modal)}
              >
                Ã—
              </button>
              <img src={modalData.image} alt={modalData.title} />
            </div>
          </div>
        </div>
      )}
    </>
  );
}
